import React, { Component } from 'react';
import './App.css';

class Home extends React.Component {

  constructor(){
      super()
      this.state = {
        firstValue:0,
        secondValue:0,
        count:0
      }
    }

  increment(){
    this.setState((prevState) => ({count: prevState.count + 1}))
  }

  decrement(){
    this.setState((prevState) => {
      if(prevState.count > 0){
        return{count: prevState.count - 1}
      }
     })
  }

  render(){

    return(
     <div>
      <h1>Calculatrice</h1>
      <div>
        <input></input>
        <br/>
        <button id='btn7' onClick={() => this.seven()}>7</button>
        <button id='btn8' onClick={() => this.eight()}>8</button>
        <button id='btn9' onClick={() => this.nine()}>9</button>
        <button id='btnPlus' onClick={() => this.increment()}>+</button>
        <br/>
        <button id='btn4' onClick={() => this.four()}>4</button>
        <button id='btn5' onClick={() => this.five()}>5</button>
        <button id='btn6' onClick={() => this.six()}>6</button>
        <button id='Minus' onClick={() => this.decrement()}>-</button>
        <br/>
        <button id ='btn1' onClick={() => this.one()}>1</button>
        <button id='btn2' onClick={() => this.two()}>2</button>
        <button id='btn3' onClick={() => this.three()}>3</button>
        <button id='btnMult' onClick={() => this.multiply()}>x</button>
        <br/>
        <button id='btn0' onClick={() => this.zero()}>0</button>
        <button id='btnEqual' onClick={() => this.equal()}>=</button>
        <button id='btnDeci' onClick={() => this.decimal()}>.</button>
        <button id='btnDevi' onClick={() => this.divide()}>/</button>
        <h1>{this.state.count}</h1>
      </div>
      <div id='buttonCalc'>

      </div>
     </div>
    )
  }
}

export default Home;
