import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class Home extends React.Component {

  constructor(){
      super()
      this.state = {
        count:0
      }
    }

    increment(){
    this.setState((prevState) => ({count: prevState.count + 1}))
  }

  decrement(){
    this.setState((prevState) => {
      if(prevState.count > 0){
        return{count: prevState.count - 1}
      }
     })
  }

  render(){

    return(
     <div>
      <h1>Calculatrice</h1>
      <div>
        <input></input>
        <br/>
        <button>7</button>
        <button>8</button>
        <button>9</button>
        <button id='Plus' onClick={() => this.increment()}>+</button>
        <br/>
        <button>4</button>
        <button>5</button>
        <button>6</button>
        <button id='Minus' onClick={() => this.decrement()}>-</button>
        <br/>
        <button>1</button>
        <button>2</button>
        <button>3</button>
        <button id='Multiply' onClick={() => this.multiply()}>x</button>
        <br/>
        <button>0</button>
        <button>=</button>
        <button id='Decimal' onClick={() => this.decimal()}>.</button>
        <button>/</button>
        <h1>{this.state.count}</h1>
      </div>
      <div id='buttonCalc'>

      </div>
     </div>
    )
  }
}

ReactDOM.render(<Home />, document.getElementById('app'))

export default App;
