import React, { Component } from 'react';
import './App.css';

const DisplayWindow = (props) => (<input type='text' value={props.expression} disabled='true'/>);

class Button extends React.Component {
  constructor() {
    super();

    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.props.onKeyPressed(this.props.text);
  }

  render() {
    return <button className="calc_btn btn btn-dark" onClick={this.onClick}>{this.props.text}</button>
  }
}

class Home extends React.Component {
  constructor () {
    super();

    this.state = {
      expression: ''
    }

    this.onKeyPressed = this.onKeyPressed.bind(this);
    this.onEvaluatePressed = this.onEvaluatePressed.bind(this);
    this.onDeletePressed = this.onDeletePressed.bind(this);
  }

  onKeyPressed(text) {
    this.setState((prev) => ({expression: prev.expression + text}));
  }

  onEvaluatePressed() {
    const result = eval(this.state.expression);
    this.setState({expression: result.toString()});
  }

  onDeletePressed() {
    this.setState((prev) => ({
      expression: prev.expression.length <= 1 ? '' : prev.expression.slice(0, -1)}));
  }

  render() {
    let numberKeysThree = [];
    for(let i =1; i < 4; i++) {
      numberKeysThree.push(<Button text={i} onKeyPressed={this.onKeyPressed}/>);
    }

    let numberKeysSix = [];
    for(let i =4; i < 7; i++) {
      numberKeysSix.push(<Button text={i} onKeyPressed={this.onKeyPressed}/>);
    }

    let numberKeysNine = [];
    for(let i =7; i < 10; i++) {
      numberKeysNine.push(<Button text={i} onKeyPressed={this.onKeyPressed}/>);
    }

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <h1 id="title">Calculatrice:</h1>
          </div>
          <div className="col-md-4"></div>
          <div className="col-md-4 center-block">
            <table className="calculatrice" id="calc">
              <tbody>
                <tr>
                  <td colSpan="4" className="calc_td_resultat">
                    <DisplayWindow expression={this.state.expression}/>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button text="AC" onKeyPressed={this.onDeletePressed}/>
                  </td>
                  <td>
                    <Button text="%" onKeyPressed={this.onKeyPressed}/>
                  </td>
                  <td>
                    <Button text="/" onKeyPressed={this.onKeyPressed}/>
                  </td>
                  <td>
                    <Button text="." onKeyPressed={this.onKeyPressed}/>
                  </td>
                </tr>
                <tr>
                  <td>
                    {numberKeysNine}
                  </td>
                  <td className="calc_td_btn">
                    <Button className="calc_btn btn btn-dark" text="+" onKeyPressed={this.onKeyPressed}/>
                  </td>
                </tr>
                <tr>
                  <td>
                  {numberKeysSix}
                  </td>

                  <td>
                    <Button text="-" onKeyPressed={this.onKeyPressed}/>
                  </td>
                </tr>
                <tr>
                  <td>
{numberKeysThree}
                  </td>

                  <td>
                    <Button text="*" onKeyPressed={this.onKeyPressed}/>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button className="calc_btn btn btn-dark" text="=" onKeyPressed={this.onEvaluatePressed}/>
                  </td>
                  <td colSpan="4">
                    <Button className="calc_btn_0 btn btn-dark" text="0" onKeyPressed={this.onKeyPressed}/>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    )
  }
}

export default Home;
