import React, { Component } from 'react';
import './App.css';

const DisplayWindow = (props) => (<input type="text" name="calc_resultat" value={this.props.expression}  id="calc_resultat" className="calc_resultat form-control" readOnly/>);

class Button extends React.Component{
  constructor() {
    super();

    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.props.onKeyPressed(this.props.text);
  }

  render() {
    return <button onClick={this.onClick}>{this.props.text}</button>;
  }
}

class Home extends React.Component {

  constructor () {
    super();

    this.state = {
      expression: ''
    }

    this.onKeyPressed = this.onKeyPressed.bind(this);
    this.onEvaluatePressed = this.onEvaluatePressed.bind(this);
    this.onDeletePressed = this.onDeletePressed.bind(this);
  }

  onKeyPressed(text) {
    this.setState((prev) => ({expression: prev.expression + text}));
  }

  onEvaluatePressed() {
    const result = eval(this.state.expression);
    this.setState({expression: result.toString()});
  }

  onDeletePressed() {
    this.setState((prev) => ({
      expression: prev.expression.length <= 1 ? '' : prev.expression.slice(0, -1)}));
  }

  render(){

    let numberKeys = [];
    for(let i =0; i < 10; i++) {
      numberKeys.push(<Button text={i} onKeyPressed={this.onKeyPressed}/>);
    }

    return(
     <div className="container">
      <div className="row">
        <div className="col-md-12">
        <h1 id="title">Calculatrice:</h1>
        </div>
        <div className="col-md-4"></div>
        <div className="col-md-4 center-block">
          <table className="calculatrice" id="calc">
            <tbody>
              <tr>
                <td colSpan="4" className="calc_td_resultat">
                <DisplayWindow expression={this.state.expression}/>
                </td>
              </tr>
              <tr>
                <td className="calc_td_btn" id="AC">
                  <button className="calc_btn btn btn-dark" text="AC" onClick={this.onClick}>AC</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="percent" text="%" onClick={() => this.percent()}>%</button>
                </td>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="divide" text="/" onClick={() => this.divide()}>/</button>
                </td>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="null" onClick={() => this.null()}>:/</button>
                </td>
              </tr>
              <tr>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="seven" text="7" onClick={this.onClick}>7</button>
                </td>
                  <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="eight" text="8" onClick={this.onClick}>8</button>
                  </td>
                  <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="nine" text="9" onClick={this.onClick}>9</button>
                  </td>
                  <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="plus" text="+" onClick={this.onClick}>+</button>
                  </td>
              </tr>
              <tr>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="four" text="4" onClick={this.onClick}>4</button>
                </td>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="five" text="5" onClick={this.onClick}>5</button>
                </td>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="six" text="6" onClick={this.onClick}>6</button>
                </td>
                <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark"id="minus" text="-" onClick={this.onClick}>-</button>
                </td>
              </tr>
              <tr>
              <td className="calc_td_btn">
                <button className="calc_btn btn btn-dark" id="one" text="1" onClick={this.onClick}>1</button>
              </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="two" text="2" onClick={this.onClick}>2</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="three" text="3" onClick={this.onClick}>3</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="multiply" text="*" onClick={() => this.multiply()}>*</button>
                </td>
              </tr>
              <tr>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="zero" text="0" onClick={this.onClick}>0</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="change" onClick={this.onClick}>+/-</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="float" text="," onClick={this.onClick}>,</button>
                </td>
                <td className="calc_td_btn">
                  <button className="calc_btn btn btn-dark" id="equal" text="=" onClick={this.onClick}>=</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        <div className="col-md-4"></div>
      </div>
     </div>
    )
  }
} 

export default Home;
