import React, { Component } from 'react';
import './App.css';

class Home extends React.Component {

  constructor(){
      super()
      this.state = {
        firstValue:0,
        secondValue:0,
        count:0
      }
    }

  increment(){
    this.setState((prevState) => ({count: prevState.count + 1}))
  }

  decrement(){
    this.setState((prevState) => {
      if(prevState.count > 0){
        return{count: prevState.count - 1}
      }
     })
  }

  zero(){
    this.setState((prevValue) =>{
      if(prevValue.firstValue !== -1){
        return{firstValue: 0}
      }
      else{
        return{secondValue: 0}
      }
    })
  }

  render(){

    return(
     <div className="container">
      <div className="row">
        <div className="col-md-12">
        <h1 id="title">Calculatrice:</h1>
        </div>
        <div className="col-md-4"></div>
        <div className="col-md-4 center-block">
          <table class="table">
            <thead>
              <tr>
                <th scope="col"></th>
                <th scope="col"><button>AC</button></th>
                <th scope="col"><input></input></th>
              </tr>
            </thead>
            <tbody>
              <tr>
              <th scope="col"></th>
                <th scope="col"><button>AC</button></th>
                <th scope="col"><button>AC</button></th>
                <th scope="col"><button>AC</button></th>
              </tr>
              <tr>
                <th scope="row">2</th>
                <td>Jacob</td>
                <td>Thornton</td>
                <td>@fat</td>
              </tr>
              <tr>
                <th scope="row">3</th>
                <td>Larry</td>
                <td>the Bird</td>
                <td>@twitter</td>
              </tr>
            </tbody>
          </table>
        </div>
        <div class="col-md-4"></div>
      </div>
     </div>
    )
  }
}

export default Home;

